#include <iostream>
#include <string>

using namespace std;

int n, m, h, sum = 0;
int block[51][11] = {0};

void top(int stu, int height) {
    if (block[stu][0] == 0)
        return;
    for (int i = 1; i <= block[stu][0] + 1; i++) {
        if (block[stu][i] + height == h) {
            sum++;
            continue;
        }
        if (block[stu][i] + height < h) {
            top(stu + 1, block[stu][i] + height);
        }
        if (block[stu][i] + height > h) {
            continue;
        }
    }
    return;
}

int main() {
    string input;
    cin >> n >> m >> h;
    cin.ignore();
    for (int i = 0; i < n; i++) {
        getline(cin, input);
        block[i][0] = input.length() / 2 + 1;
        for (int j = 1; j <= input.length() / 2 + 1; j++) {
            block[i][j] = (int) input[(j - 1) * 2] - '0';
        }
    }
    top(0, 0);
    cout << sum;

}

