#include <iostream>
#include <string>
#include <vector>

using namespace std;

int n, m, h, sum = 0;
//int block[51][11] = {0};
int dp[51][1001] = {0};

int main() {
    string input;
    vector<int> block[51];
    cin >> n >> m >> h;
    cin.ignore();
    for (int i = 1; i <= n; i++) {
        getline(cin, input);
        for (int j = 1; j <= input.length() / 2 + 1; j++) {
            block[i].push_back((int) input[(j - 1) * 2] - '0');
        }
    }


    for (int i = 0; i <= n; i++)
        dp[i][0] = 1;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= h; j++) {
            for (int k = 0; k < block[i].size(); k++) {
                if (j >= block[i][k]) {
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - block[i][k]]) % 10007;
                }
            }
            dp[i][j] = (dp[i][j] + dp[i - 1][j]) % 10007;
        }
    }
    cout << dp[n][h];

}

