@Async //데이터 조회 쿼리
    public CompletableFuture<List<Order>> findContent(Pageable pageable, String address) {
        QOrder order = QOrder.order;
        QUser user = QUser.user;
        QCoupon coupon = QCoupon.coupon;

        List<Order> content = queryFactory
            .select(order)
            .from(order)
            .innerJoin(user).on(order.userId.eq(user.id))
            .leftJoin(coupon).on(order.couponId.eq(coupon.id))
            .where(order.address.eq(address))
            .offset(pageable.getOffset())
            .limit(pageable.getPageSize())
            .fetch();

        return CompletableFuture.completedFuture(content);
    }

    @Async //데이터 개수 세는 쿼리
    public CompletableFuture<Long> findTotalCount(String address) {
        QOrder order = QOrder.order;

        Long totalCount = queryFactory
            .select(order.count())
            .from(order)
            .where(order.address.eq(address))
            .fetchOne();

        return CompletableFuture.completedFuture(totalCount);
    }

    @Override //페이지로 반환
    public Page<Order> findPagingBy(Pageable pageable, String address) {
        try {
            // 두 쿼리를 비동기적으로 실행하고 결과를 기다림
            CompletableFuture<List<Order>> contentFuture = findContent(pageable, address);
            CompletableFuture<Long> totalCountFuture = findTotalCount(address);

            // 두 CompletableFuture가 모두 완료될 때까지 기다림
            CompletableFuture.allOf(contentFuture, totalCountFuture).join();

            List<Order> content = contentFuture.get();
            Long totalCount = totalCountFuture.get();

            return new PageImpl<>(content, pageable, totalCount);
        } catch (Exception e) {
            throw new RuntimeException("Failed to execute queries asynchronously", e);
        }
    }

