text.content:
@Component
class A {
    private final B b;

    // 기본 생성자
    public A() {
        this.b = null; // 기본 생성자에서는 b가 주입되지 않음
    }

    // 의존성을 주입받는 생성자
    @Autowired
    public A(B b) {
        this.b = b;
    }

    public void funcA() {
        if (b != null) {
            b.funcB();
        } else {
            System.out.println("B is not initialized");
        }
    }
}

