public class ATest {

    @Mock //B 클래스 모킹
    private B b;

    @InjectMocks //A 클래스에 B 클래스 주입
    private A a;

    @Test
    void testFuncA() {
        // Mockito가 위의 어노테이션을 처리하도록 지시
        MockitoAnnotations.openMocks(this);

        // 테스트를 위해 메서드 호출
        a.funcA();

        // B 클래스의 funcB 메서드가 호출되었는지 확인
        verify(b).funcB();
    }
}

