import java.util.*;

class Solution {
		//5, 55, 555와 같은 반복수 생성 함수
    public int repeat(int N, int i){
        int num = 0;
        for(int j=0;j<i;j++)
            num=num*10+N;
        return num;
    }

    public int solution(int N, int number) {
        int answer = -1;

        List<Set> dp = new ArrayList<>();
        for(int i=0;i<=8;i++){
            dp.add(new HashSet<Integer>());
        }

        for(int i=1;i<=8;i++){
            Set<Integer> cur = dp.get(i);

            cur.add(repeat(N,i));

            for(int j=1;j<i;j++){
		            //불필요한 배열로 변경하지 않고 Set 그대로 사용
                Set<Integer> A = dp.get(j);
                Set<Integer> B = dp.get(i-j);

                for(int a : A){
                    for(int b : B){
                        cur.add(a+b);
                        cur.add(a*b);
                        cur.add(a-b);
                        if(b!=0)
                            cur.add(a/b);
                    }
                }
            }

            if(cur.contains(number))
                return i;
        }

        return answer;
    }
}

