class Solution {
    public long solution(int[] sequence) {
        long answer = 0;
        int len = sequence.length;

        long max = 0L;
        long min = 0L;
        long sum = 0L;
        for(int i=0;i<len;i++){
            if(i%2==0)
                sum += sequence[i];
            else
                sum -= sequence[i];

            max = Math.max(max, sum);
            min = Math.min(min, sum);
        }

        return max - min;
    }
}

