import java.util.*;

class Solution {
    public List<List<Integer>> arr;
    public Set<Integer> unVisited;
    public boolean[] lighted;

    public void visit(int n){
        List<Integer> cur = arr.get(n);
        unVisited.remove(n);
        for(int i=0;i<cur.size();i++){
            if(unVisited.contains(cur.get(i))){
                unVisited.remove(cur.get(i));
            }
        }
    }

    public int count(int n){
        List<Integer> cur = arr.get(n);
        int sum = 0;
        for(int i=0;i<cur.size();i++){
            if(unVisited.contains(cur.get(i)))
                sum++;
        }
        return sum;
    }

    public int solution(int n, int[][] lighthouse) {
        int answer = 0;

        arr = new ArrayList<>();
        unVisited = new HashSet<>();
        for(int i=0;i<=n;i++){
            arr.add(new ArrayList<Integer>());
            unVisited.add(i);
        }
        unVisited.remove(0);

        for(int i=0;i<lighthouse.length;i++){
            arr.get(lighthouse[i][0]).add(lighthouse[i][1]);
            arr.get(lighthouse[i][1]).add(lighthouse[i][0]);
        }


        lighted = new boolean[n+1];

        for(int i=1;i<=n;i++){
            if(arr.get(i).size()==1){
                unVisited.remove(i);
                int neighbor = arr.get(i).get(0);

                if(!lighted[neighbor]){
                    answer++;
                    lighted[neighbor] = true;    

                    visit(neighbor);
                }
            }
        }

        while(!unVisited.isEmpty()){
            // for(int cur: unVisited){
            //     System.out.print(cur+" ");
            // }
            // System.out.println();


            int max = -1;
            int num = 0;
            for(int cur : unVisited){
                int now = count(cur);
                if(max<now){
                    max = now;
                    num = cur;
                }
            }

            visit(num);
            answer++;
        }

        return answer;
    }
}
