import java.util.*;

class Solution {
    public int solution(int n, int[][] lighthouse) {
        int answer = 0;

        List<Integer>[] arr = new ArrayList[n+1];
        for(int i=1;i<=n;i++){
            arr[i] = new ArrayList<>();
        }

        for(int[] now : lighthouse){
            arr[now[0]].add(now[1]);
            arr[now[1]].add(now[0]);
        }

        //각 노드의 차수 배열
        int[] deg = new int[n+1];
        //리프 노드 저장 큐
        Deque<Integer> q = new ArrayDeque<>();

        for(int i=1;i<=n;i++){
            deg[i] = arr[i].size();
            if(deg[i]==1)
                q.add(i);
        }

        boolean[] on = new boolean[n+1];

        while(!q.isEmpty()){
            int leaf = q.poll();
            //리프노드 아니라면 넘어감
            if(deg[leaf]!=1)
                continue;

            //부모노드 찾기
            int parent = -1;
            for(int p : arr[leaf]){
                if(deg[p]>0){
                    parent = p;
                    break;
                }
            }
            //부모 노드가 없다면 이미 연결된 상태임
            if(parent==-1){
                deg[leaf] = 0;
                continue;
            }

            //부모 노드가 안켜져 있다면 켜주기
            if(!on[parent]){
                on[parent] = true;
                answer++;
            }

            //부모 노드와 연결된 간선들 제거
            for(int nb : arr[parent]){
                if(deg[nb]>0){
                    deg[nb]--;
                    if(deg[nb]==1)
                        q.add(nb);
                }
            }
            deg[parent] = 0;
        }

        return answer;
    }
}

