import java.util.*;

class Solution {
    public int[] arr;
    public int n;

    public int last(int k){
        for(int i=k+1;i<n;i++){
            if(arr[i]!=-1)
                return i;
        }
        for(int i=k-1;i>=0;i--){
            if(arr[i]!=-1)
                return i;
        }
        return 0;
    }

    public int move(int k, int num, int dir){
        for(int i=k+dir;i>=0 && i<n;i+=dir){
            if(arr[i]==-1){
                continue;
            }
            num--;
            if(num==0)
                return i;
        }
        return k;
    }

    public String solution(int n, int k, String[] cmd) {
        arr = new int[n];
        this.n = n;
        for(int i=0;i<n;i++)
            arr[i] = i;

        Deque<Integer> stack = new ArrayDeque<>();

        for(int i=0;i<cmd.length;i++){
            int num;
            switch(cmd[i].charAt(0)){
		            //위로 이동
                case 'U':
                    num = Integer.parseInt(cmd[i].substring(2).trim());
                    k = move(k, num, -1);
                    break;
                //아래로 이동
                case 'D':
                    num = Integer.parseInt(cmd[i].substring(2).trim());
                    k = move(k, num, 1);
                    break;
                //삭제 후 다음 행으로 이동
                case 'C':
                    arr[k] = -1;
                    stack.push(k);
                    k = last(k);
                    break;
                //최근 삭제된 행 복구
                case 'Z':
                    int redo = stack.pop();
                    arr[redo] = redo;
                    break;
            }
        }
        StringBuilder sb = new StringBuilder();
        for(int i=0;i<n;i++){
            if(arr[i]!=-1)
                sb.append("O");
            else
                sb.append("X");
        }

        return sb.toString();
    }
}

