class Solution {
    public int solution(int[][] board, int[][] skill) {
        int answer;
        int r = board.length;
        int c = board[0].length;
        answer = r*c;
        int[][] diff = new int[r+1][c+1];

        for(int[] now : skill){
            int r1 = now[1];
            int r2 = now[3];
            int c1 = now[2];
            int c2 = now[4];
            //공격이면 음수, 회복이면 양수
            int num = now[0] == 1 ? now[5]*-1 : now[5];

            //네 꼭짓점 업데이트
            diff[r1][c1] += num;
            diff[r1][c2+1] -= num;
            diff[r2+1][c1] -= num;
            diff[r2+1][c2+1] += num;
        }

        //가로 방향 누적합
        for(int i=0;i<r+1;i++){
            for(int j=1;j<c+1;j++){
                diff[i][j] += diff[i][j-1];
            }
        }

        //세로 방향 누적합
        for(int j=0;j<c+1;j++){
            for(int i=1;i<r+1;i++)
                diff[i][j] += diff[i-1][j];
        }

        //최종 건물 내구도 구하기
        for(int i=0;i<r;i++){
            for(int j=0;j<c;j++){
                board[i][j] += diff[i][j];
                //내구도가 0 이하면 파괴된 건물
                if(board[i][j]<=0)
                    answer--;
            }
        }

        return answer;
    }
}

