import java.util.*;

class Solution {
    public long solution(int[] a, int[][] edges) {
        long answer = 0;
        int n = a.length;
        long sum = 0;
        long[] w = new long[n]; // 노드 가중치를 long으로 저장
        int[] degree = new int[n]; // 각 노드의 차수

        // 전체 합 계산 및 long 배열 초기화
        for(int i=0;i<n;i++){
            sum += a[i];
            w[i] = a[i];
        }

        // 합이 0이 아니면 불가능
        if(sum!=0)
            return -1;

        List<Integer>[] tree = new ArrayList[n];
        for(int i=0;i<n;i++)
            tree[i] = new ArrayList<>();

        // 간선 정보 저장
        for(int i=0;i<edges.length;i++){
            int c = edges[i][0];
            int b = edges[i][1];
            tree[c].add(b);
            tree[b].add(c);
            degree[c]++;
            degree[b]++;
        }

        // 리프 노드부터 시작
        Queue<Integer> leaf = new ArrayDeque<>();
        for(int i=0;i<n;i++){
            if(degree[i]==1)
                leaf.add(i);
        }

        boolean[] visited = new boolean[n];

        // 리프부터 차근차근 부모 방향으로 값 전달
        while(!leaf.isEmpty()){
            int now = leaf.poll(); // 현재 리프 노드
            long val = w[now]; // 현재 노드의 가중치
            w[now] = 0; // 현재 노드는 0으로 만들기
            visited[now] = true;
            answer += Math.abs(val); // 이동한 절댓값 누적

            // 연결된 부모 노드에 값 전달
            for(int next : tree[now]){
                if(!visited[next]){
                    w[next] += val; // 부모에 현재 값 더하기
                    degree[next]--; // 부모 차수 감소
                    if(degree[next]==1) // 새 리프가 되면 큐에 추가
                        leaf.add(next);
                }
            }
        }

        return answer;
    }
}

