import java.util.*;

class Solution {
    public int[][] solution(int[][] rc, String[] operations) {
        int r = rc.length;
        int c = rc[0].length;

        int[][] answer = new int[r][c];

        ArrayList<ArrayDeque> a = new ArrayList<>();

        for(int i=0;i<=r;i++){
            a.add(new ArrayDeque<Integer>());
        }

        for(int i=0;i<r;i++){
            for(int j=0;j<c;j++){
                a.get(i).add(rc[i][j]);
            }
        }

        for(int i=0;i<operations.length;i++){
            String op = operations[i];

            if(op.equals("Rotate")){
                for(int j=0;j<r;j++){
                    if(j!=0)
                        a.get(j).removeFirst();
                    if(j==r-1)
                        break;
                    a.get(j).addFirst(a.get(j+1).getFirst());
                }
                for(int j=r-1;j>=0;j--){
                    if(j!=r-1)
                        a.get(j).removeLast();
                    if(j==0)
                        break;
                    a.get(j).addLast(a.get(j-1).getLast());
                }
            } 
            else{
                ArrayDeque<Integer> rm = a.remove(r-1);
                a.add(0, rm);
            }
        }

        for (int i = 0; i < r; i++) {
            int size = a.get(i).size();
            answer[i] = new int[size];
            int idx = 0;
            for (int val : (ArrayDeque<Integer>) a.get(i)) {
                answer[i][idx++] = val;
            }
        }
        return answer;
    }
}

